/**
 * BootstrapIcons component
 * 
 * @param {Object} props Component properties
 * @param {string} props.icon Icon name to render
 * @param {number} props.size Icon size in pixels
 * @param {string} props.color Icon color
 */
export const BootstrapIcons = ({ icon, size = 24, color = 'currentColor' }) => {
    // Default icon if the requested one doesn't exist
    const defaultIcon = {
        path: 'M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z',
        viewBox: '0 0 16 16'
    };

    // Get the icon path or use default
    const selectedIcon = iconPaths[icon] || defaultIcon;

    return (
        <SVG
            width={size}
            height={size}
            viewBox={selectedIcon.viewBox}
            xmlns="http://www.w3.org/2000/svg"
            fill={color}
        >
            <Path d={selectedIcon.path} />
        </SVG>
    );
};